mapper.select:
SELECT COUNT(id)
FROM message
WHERE status = 0
AND from_id != 1
AND to_id = #{userId}

    AND conversation_id = #{conversationId}

mapper.update:
update message set status = #{status}
where id in

    #{id}

mapper.select:
select 
from message
where id in(
    select max(id) from message
    where status != 2
    and from_id = 1
    and to_id = #{userId}
    and conversation_id = #{topic}
)

